import React from 'react';

const emailRegex = RegExp(
  /^[a-zA-Z0-9.!#$%&’*+/=?^_`{|}~-]+@[a-zA-Z0-9-]+(?:\.[a-zA-Z0-9-]+)*$/
);

const Users = class userInput extends React.Component {
  state = {
    fullName: '',
    userId: '',
    email: '',
    password: '',
    passwordCheck: '',
    accessType: 'updates',
    formsError: {
      fullName: false,
      userId: false,
      email: false,
      password: false,
      passwordCheck: false,
      accessType: false
    },
    showError: true
  };

  handleChange = event => {
    const { name, value } = event.target;
    const { formsError } = this.state;

    // Handle input field rules
    switch (name) {
      case 'fullName':
        formsError[name] = value.length < 3 ? 'minimum 3 characaters' : false;
        break;
      case 'userId':
        formsError[name] = value.length < 3 ? 'minimum 3 characaters' : false;
        break;
      case 'email':
        formsError[name] = emailRegex.test(value)
          ? false
          : 'invalid email address';
        break;
      case 'password':
        formsError[name] = value.length < 6 ? 'minimum 6 characaters' : false;
        break;
      case 'passwordCheck':
        formsError[name] = value.length < 6 ? 'minimum 6 characaters' : false;
        break;
      case 'accessType':
        formsError[name] =
          value === 'updates'
            ? false
            : value === 'premium'
            ? false
            : 'You must choose one option';
        break;
    }

    this.setState({ [name]: value, formsError });
  };

  handleSubmit = event => {
    event.preventDefault();
    const error = Object.value(this.state.formsError).find(
      error => error === true
    );
    console.log('error', error);

    // console.log(this.state.formsError);
    // if( Object.value(this.state.formsError) )
  };

  render() {
    console.log(this.state);
    return (
      <div>
        <h1>USERS</h1>
        <form onSubmit={this.handleSubmit}>
          <label htmlFor='fullName'>Full Name:</label>
          {this.state.showError && this.state.formsError.fullName}
          <input
            id='fullName'
            placeholder='Full Name'
            name='fullName'
            type='text'
            value={this.state.fullName}
            onChange={this.handleChange}
          />

          <label htmlFor='userId'>User ID: </label>
          <input
            id='userId'
            placeholder='User ID'
            name='userId'
            type='text'
            value={this.state.userId}
            onChange={this.handleChange}
          />

          <label htmlFor='email'>Email:</label>
          <input
            id='email'
            placeholder='Email'
            name='email'
            type='email'
            value={this.state.email}
            onChange={this.handleChange}
          />

          <label htmlFor='password'>Password:</label>
          <input
            id='password'
            placeholder='Password'
            name='password'
            type='password'
            value={this.state.password}
            onChange={this.handleChange}
          />

          <label htmlFor='passwordCheck'>Confirm Password:</label>
          <input
            id='passwordCheck'
            placeholder='Confirm Password'
            name='passwordCheck'
            type='password'
            value={this.state.passwordCheck}
            onChange={this.handleChange}
          />

          <label htmlFor='accessType'>Access Type:</label>
          <select
            id='accessType'
            name='accessType'
            value={this.state.accessType}
            onChange={this.handleChange}>
            <option value='updates'>Updates Only</option>
            <option value='premium'>Premiun Access</option>
          </select>

          <button type='submit'>Create New User</button>
        </form>
      </div>
    );
  }
};

export default Users;
